using Application.DTOs; using FluentValidation; namespace Application.Validators { public class AdvertisementValidator : AbstractValidator<AdvertisementDto> { public AdvertisementValidator() { RuleFor(x => x.Title).NotEmpty().MaximumLength(100); RuleFor(x => x.Content).NotEmpty(); RuleFor(x => x.UserId).GreaterThan(0); RuleFor(x => x.PhoneNumber).NotEmpty().MaximumLength(20); } } }