using Application.DTOs; using FluentValidation; namespace Application.Validators { public class PublicServiceValidator : AbstractValidator<PublicServiceDto> { public PublicServiceValidator() { RuleFor(x => x.Name).NotEmpty().MaximumLength(100); RuleFor(x => x.Category).GreaterThan(0); RuleFor(x => x.PhoneNumber).NotEmpty().MaximumLength(20); RuleFor(x => x.Status).NotEmpty().MaximumLength(50); } } }